CREATE TABLE [Films] (    [id] INTEGER PRIMARY KEY AUTOINCREMENT,    [title] NVARCHAR(255),    [episode_id] INTEGER,    [opening_crawl] NVARCHAR(255),    [director] NVARCHAR(255),    [producer] NVARCHAR(255),    [release_date] TIMESTAMP );
CREATE TABLE [People] (    [id] INTEGER PRIMARY KEY AUTOINCREMENT,    [name] NVARCHAR(255),    [birth_year] NVARCHAR(255),    [eye_color] NVARCHAR(255),    [gender] NVARCHAR(255),    [hair_color] NVARCHAR(255),    [height] NVARCHAR(255),    [mass] NVARCHAR(255),    [skin_color] NVARCHAR(255),    [planet_id] INTEGER,    FOREIGN KEY ([planet_id]) REFERENCES [Planets] ([id]) );
CREATE TABLE [Planets] (    [id] INTEGER PRIMARY KEY AUTOINCREMENT,    [name] NVARCHAR(255),    [diameter] NVARCHAR(255),    [rotation_period] NVARCHAR(255),    [orbital_period] NVARCHAR(255),    [gravity] NVARCHAR(255),    [population] NVARCHAR(255),    [climate] NVARCHAR(255),    [terrain] NVARCHAR(255),    [surface_water] NVARCHAR(255) );
CREATE TABLE [Films_People] (    [id] INTEGER PRIMARY KEY AUTOINCREMENT,    [film_id] INTEGER,    [person_id] INTEGER,    FOREIGN KEY ([film_id]) REFERENCES [Films] ([id]),    FOREIGN KEY ([person_id]) REFERENCES [People] ([id]) );
CREATE TABLE [Films_Planets] (    [id] INTEGER PRIMARY KEY AUTOINCREMENT,    [film_id] INTEGER,    [planet_id] INTEGER,    FOREIGN KEY ([film_id]) REFERENCES [Films] ([id]),    FOREIGN KEY ([planet_id]) REFERENCES [Planets] ([id]) );